class PutEntitiesInOneArray(StreamCallback):
  forceArray = []
  def __init__(self):
    forceArrayStr = os.environ.get('FORCE_ARRAY', '')
    self.forceArray = forceArrayStr.split(',')

  def cleanKeys(self, subTmp):
    if type(subTmp) == list:
      result = []
      for i in subTmp:
        result.append(self.cleanKeys(subTmp))
      return result
    elif type(subTmp) == dict:
      result = {}
      for key, value in subTmp.items():
        key = key.split('/')[-1].split(':')[-1].split('#')[-1]
        result[key] = self.cleanKeys(value)
      return result
    else:
      return subTmp

  def getAttribute(self, value, attribKey):
    log.warn('attrib key: ' + attribKey)
    log.warn('value: ' + str(value))
    if type(value) == list:
      result = []
      for entry in value:
        subTmp = self.getAttribute(entry, attribKey)
        if subTmp:
          result.append(subTmp)
    elif type(value) == dict:
      result = {}
      for key, value in value.items():
        log.warn('sub key: ' + key)
        if not value:
          log.warn('value is none')  
        else:
          log.warn('value: ' + str(value)) 
        if key in ['type', 'createdAt', 'observedAt', 'modifiedAt']:
          log.warn('continue type ca oa ma')
          continue
        if key in ['value', 'object']:
          log.warn('value found')
          result['value'] = value
        else:
          log.warn('else ')
          subTmp = self.getAttribute(value, key)
          if subTmp:
            result[key] = subTmp
      if 'value' not in result.keys():
        log.warn('value not in result')
        log.warn(str(result))
        return None
      if len(result) == 1:
        result = result['value']
      else:
        if type(result['value']) == list:
          tmp = result['value']
          del result['value']
          tmp.append(result)
          result = tmp
        elif type(result['value']) == dict:
          tmp = result['value']
          del result['value']
          tmp.update(result)
          result = tmp
    else:
      result = value
    if attribKey in self.forceArray and type(result) != list:
      result = [result]
    return result
  def process(self, inputStream, outputStream):
    # Read input stream as JSON object
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    entities = json.loads(text)
    result = []
    for entity in entities:
      newEntity = {}
      if 'resourceType' not in entity.keys():
        entity['resourceType'] = {'type': 'Property', 'value': entity['type']}
      for key, value in entity.items():
        if key in ['id']:
          newEntity[key] = value
          continue
        if key in ['observedAt', 'modifiedAt', 'createdAt', 'type']:
          continue
        key = key.split('/')[-1].split(':')[-1].split('#')[-1]
        log.warn('calling from entity root with key ' + key)
        subTmp = self.getAttribute(value, key)
        if subTmp:
          log.warn('before clean')
          log.warn(str(subTmp))
          subTmp = self.cleanKeys(subTmp)
          log.warn('after clean')
          log.warn(str(subTmp))
          newEntity[key] = subTmp
      result.append(newEntity)  

    outputStream.write(bytearray(json.dumps(result, indent=4).encode('utf-8'))) 

