class TransformJSON(StreamCallback):
  def __init__(self):
    pass

  def process(self, inputStream, outputStream):
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    entity = json.loads(text)
    template = {'id': entity['id'], 'type': entity['type']}
    tmpList = []
    for attrsKey, attrsValue in entity.items():
      if attrsKey in ['id', 'type', 'modifiedAt', 'createdAt', 'observedAt']:
        continue
      for i in range(len(attrsValue)):
        if len(tmpList) == i:
          tmpEntity = template.copy()
          tmpList.append(tmpEntity)
        else:
          tmpEntity = tmpList[i]
        if type(attrsValue) == dict:
          tmpEntity[attrsKey] = attrsValue
        else:
          tmpEntity[attrsKey] = attrsValue[i]
        if 'observedAt' in tmpEntity[attrsKey].keys():
          del tmpEntity[attrsKey]['observedAt']
        if 'instanceId' in tmpEntity[attrsKey].keys():
          del tmpEntity[attrsKey]['instanceId']
    outputStream.write(bytearray(json.dumps(tmpList, indent=4).encode('utf-8'))) 

